import { useState } from 'react';
import Input from './Input';
import formData from '../data/formData';
import formDataMonthly from '../data/formDataMonthly';
formDataYearly

function Form({ inputClasses, isClicked }) {
  const onSubmit = (e) => {
    e.preventDefault();
  };
  const [checkInput, setCheckInput] = useState(9);
  const [yearlyList, setyearlyList] = useState(true);
  function checkedClick() {
    setyearlyList(!yearlyList);
  }
  function monthlyPage() {
    return formDataMonthly.map((item) => {
      return (
        <div key={item.value}>
          <Input
            checked={item.value === checkInput}
            labelSpan={item.labelSpan}
            spanClass={item.spanClass}
            labelName={item.labelName}
            labelClass={item.labelClass}
            nameInput={item.labelName}
            typeInput={item.typeInput}
            inputId={item.id}
            inputValue={item.value}
            onChange={(e) => setCheckInput(+e.target.value)}
          />
        </div>
      );
    });
  }
  function yearlyPage() {
    return formDataYearly.map((item) => {
      return (
        <div key={item.value}>
          <Input
            checked={item.value === checkInput}
            labelSpan={item.labelSpan}
            spanClass={item.spanClass}
            labelName={item.labelName}
            labelClass={item.labelClass}
            nameInput={item.labelName}
            typeInput={item.typeInput}
            inputId={item.id}
            inputValue={item.value}
            onChange={(e) => setCheckInput(+e.target.value)}
          />
        </div>
      );
    });
  }
  function pages(isClicked) {
    if (isClicked === 0) {
      return formData.map((e) => {
        return (
          <Input
            labelClass={e.labelClass}
            labelName={e.labelName}
            typeInput={e.typeInput}
            placeholderInput={e.placeholderInput}
            key={e.placeholderInput}
          />
        );
      });
    } else if (isClicked === 1) {
      return (
        <div className="flex-col">
          
          <div className="flex gap-[18px] mb-[32px]">{yearlyList ? monthlyPage():yearlyPage()}</div>
          <button 
      onClick={checkedClick} 
      class="btn btn-info mb-5">
      Toggle State
      </button>
          <div className="flex gap-[24px] justify-center items-center w-[450px] h-[48px]">
           <span className='switchMode'>Monthly</span>
            <label className='switch'>
              <input id='yearly' className="opacity-[0] absolute" type="checkbox"/>
            </label>
            <span className='switchMode'>Yearly</span>
          </div>
        </div>
      );
    }
  }
  return (
    <form onSubmit={onSubmit} className={inputClasses} action="#">
      {pages(isClicked)}
    </form>
  );
}

export default Form;
