import { useState } from "react";
import Input from './Input';
import formData from '../data/formData';
import formDataRadio from '../data/formDataRadio';


function Form({ inputClasses, isClicked }) {
  return <form className={inputClasses} action="#">{
  isClicked === 0 ?  formData.map((e)=>{
      return (
        <Input
        labelClass={e.labelClass}
        labelName={e.labelName}
        typeInput={e.typeInput}
        placeholderInput={e.placeholder}
        key={e.placeholderInput}
        />)
      }) : formDataRadio.map((item)=>{
        const [checkInput, setCheckInput] = useState(item.checked)
        return (
         <div key={item.value}>
            {/* <Input
            checked={e.checked}
            labelSpan={e.labelSpan}
            spanClass={e.spanClass}
            labelName={e.labelName}
            labelClass={e.labelClass}
            nameInput={e.labelName}
            typeInput={e.typeInput}
            inputId={e.id}
            inputValue={e.value}
            /> */}
             <label className={item.labelClass}>
      {item.labelName}
      <span className={item.spanClass}>{item.labelSpan}</span>
            <input 
            type={item.typeInput}
            checked={item.value === checkInput}
            onChange={e => setCheckInput(e.target.value)}
            name={item.typeInput}
        id={item.id}
        value={item.value}
            />
            </label>
         </div>)
        })}</form>}

      export default Form;
