import { useState } from "react";

function Input(props) {
  const {
    labelName,
    labelClass,
    labelSpan,
    spanClass,
    nameInput,
    typeInput,
    inputId,
    inputValue,
    placeholderInput,
    checked,
  } = props;

const [checkInput, setCheckInput] = useState(checked)
  
  // this.checkedState={checked}
let check = {}
 const onCheckedChange = (e)=>{
  const active = e.target.checked
  console.log(active);
  const name = e.target.name
 return  check = {[name]: active}
}

  return (
    <label className={labelClass}>
      {labelName}
      <span className={spanClass}>{labelSpan}</span>
      <input
        checked={checked
        }
        onChange={onCheckedChange}
        type={typeInput}
        name={nameInput}
        id={inputId}
        value={inputValue}
        placeholder={placeholderInput}
      />
    </label>
  );
}

export default Input;
