  const onSubmit = (e) => {
    e.preventDefault()}
    const [checkInput, setCheckInput] = useState(9)
  return <form onSubmit={onSubmit} className={inputClasses} action="#">{
    isClicked === 0 ?  formData.map((e)=>{
      return (
        <Input
        labelClass={e.labelClass}
        labelName={e.labelName}
        typeInput={e.typeInput}
        placeholderInput={e.placeholder}
        key={e.placeholderInput}
        />)
      }) : formDataRadio.map((item)=>{
        // console.log(checkInput);
