import { useState } from "react";

function Input(props) {
  const {
    labelName,
    labelClass,
    labelSpan,
    spanClass,
    nameInput,
    typeInput,
    inputId,
    inputValue,
    placeholderInput,
    checked,
  } = props;

const [checkInput, setCheckInput] = useState(checked)
  
 const onCheckedChange = (e)=>{
  const active = e.target.checked
  const name = e.target.name
  return  setCheckInput({[name]: active})  
}
console.log(nameInput);
// console.log(checkInput.active);

return (
  <label className={labelClass}>
      {labelName}
      <span className={spanClass}>{labelSpan}</span>
      <input
        checked={checkInput.nameInput
        }
        onChange={onCheckedChange}
        type={typeInput}
        name={nameInput}
        id={inputId}
        value={inputValue}
        placeholder={placeholderInput}
      />
    </label>
  );
}

export default Input;
