import { useState } from "react";

function Input(props) {
  const {
    labelName,
    labelClass,
    labelSpan,
    spanClass,
    // nameInput,
    typeInput,
    inputId,
    inputValue,
    placeholderInput,
    checked,
  } = props;

  
  
  const [checkInput, setCheckInput] = useState(checked)
const onCheckedChange = (e, name)=>{
  // const active = e.target.checked
  // const name = e.target.id
  console.log(e.target.value);
  console.log(e.target.id);
  // setCheckInput( active)  
  setCheckInput({[name]: e.target.id})  
  
}
console.log(checkInput);
console.log(inputId);

return (
  <label className={labelClass}>
      {labelName}
      <span className={spanClass}>{labelSpan}</span>
      <input
        checked={checkInput === inputId ? true : false}
        // disabled={checkInput !== inputId}
        // onChange={(e)=>setCheckInput(e.target.id)}
        onChange={(e)=>onCheckedChange(e, inputId)}
        type={typeInput}
        name={typeInput}
        id={inputId}
        value={inputValue}
        placeholder={placeholderInput}
      />
    </label>
  );
}

export default Input;
