import { useState } from "react";

function Input(props) {
  const {
    labelName,
    labelClass,
    labelSpan,
    spanClass,
    // nameInput,
    typeInput,
    inputId,
    inputValue,
    placeholderInput,
    checked,
  } = props;

  
  
  const [checkInput, setCheckInput] = useState(checked)
const onCheckedChange = (e)=>{
  // const active = e.target.checked
  // const name = e.target.id
  // console.log(e.target.checked);
  // console.log(e.target.id);
 
  // setCheckInput({...checked, [name]: active})  
  setCheckInput(e.target.id)  
  
  console.log(inputId);
}
console.log(checkInput);
// console.log(`${inputId}: true`);

return (
  <label className={labelClass}>
      {labelName}
      <span className={spanClass}>{labelSpan}</span>
      <input
        checked={checkInput}
        // disabled={checkInput !== inputId}
        // onChange={(e)=>setCheckInput(e.target.id)}
        onChange={onCheckedChange}
        type={typeInput}
        name={typeInput}
        id={inputId}
        value={inputValue}
        placeholder={placeholderInput}
      />
    </label>
  );
}

export default Input;
