import { useState } from "react";

function Input(props) {
  const {
    labelName,
    labelClass,
    labelSpan,
    spanClass,
    // nameInput,
    typeInput,
    inputId,
    inputValue,
    placeholderInput,
    checked,
  } = props;

const [checkInput, setCheckInput] = useState(checked)
let name = ""
 const onCheckedChange = (e)=>{
  const active = e.target.checked
   name = e.target.id
  // console.log(name);
  setCheckInput({[name]: active})  
   
}
console.log(name);
console.log(checkInput);

return (
  <label className={labelClass}>
      {labelName}
      <span className={spanClass}>{labelSpan}</span>
      <input
        checked={checkInput === name ? true : false}
        disabled={checkInput === undefined}
        onChange={onCheckedChange}
        type={typeInput}
        name={typeInput}
        id={inputId}
        value={inputValue}
        placeholder={placeholderInput}
      />
    </label>
  );
}

export default Input;
